#!/usr/bin/env python3
"""
Model prediction module for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

import joblib
import numpy as np
from pathlib import Path
from typing import Union, Optional, Any
import logging

logger = logging.getLogger(__name__)

class Predictor:
    """Model prediction class"""

    def __init__(self, model_path: Union[str, Path]):
        """Initialize predictor with trained model"""
        self.model_path = Path(model_path)
        self.model = None
        self.confidence_scores = None
        self.explanations = None

        self.load_model()

    def load_model(self):
        """Load the trained model"""

        if not self.model_path.exists():
            raise FileNotFoundError(f"Model file not found: {self.model_path}")

        logger.info(f"Loading model from: {self.model_path}")

        try:
            self.model = joblib.load(self.model_path)
            logger.info(f"Model loaded successfully: {type(self.model).__name__}")
        except Exception as e:
            logger.error(f"Failed to load model: {str(e)}")
            raise

    def predict(self, X: np.ndarray, batch_size: int = 32, 
                include_confidence: bool = False, explain: bool = False) -> np.ndarray:
        """Make predictions on input data"""

        if self.model is None:
            raise ValueError("Model not loaded")

        logger.info(f"Making predictions on {X.shape[0]} samples...")

        # Reset previous results
        self.confidence_scores = None
        self.explanations = None

        try:
            # Make predictions
            if batch_size and X.shape[0] > batch_size:
                # Batch prediction for large datasets
                predictions = []
                for i in range(0, X.shape[0], batch_size):
                    batch = X[i:i + batch_size]
                    batch_pred = self.model.predict(batch)
                    predictions.append(batch_pred)

                predictions = np.concatenate(predictions, axis=0)
            else:
                predictions = self.model.predict(X)

            # Get confidence scores if requested
            if include_confidence:
                self.confidence_scores = self._get_confidence_scores(X)

            # Get explanations if requested
            if explain:
                self.explanations = self._get_explanations(X)

            logger.info(f"Generated {len(predictions)} predictions")
            return predictions

        except Exception as e:
            logger.error(f"Prediction failed: {str(e)}")
            raise

    def predict_proba(self, X: np.ndarray) -> Optional[np.ndarray]:
        """Get prediction probabilities (for classification models)"""

        if self.model is None:
            raise ValueError("Model not loaded")

        if hasattr(self.model, 'predict_proba'):
            logger.info("Getting prediction probabilities...")
            return self.model.predict_proba(X)
        else:
            logger.warning("Model does not support probability prediction")
            return None

    def _get_confidence_scores(self, X: np.ndarray) -> Optional[np.ndarray]:
        """Calculate confidence scores for predictions"""

        try:
            # For classification models with probability support
            if hasattr(self.model, 'predict_proba'):
                probabilities = self.model.predict_proba(X)
                # Use maximum probability as confidence
                confidence = np.max(probabilities, axis=1)
                logger.info("Confidence scores calculated from probabilities")
                return confidence

            # For models with decision function
            elif hasattr(self.model, 'decision_function'):
                decision_scores = self.model.decision_function(X)
                # Normalize decision scores to confidence
                confidence = np.abs(decision_scores)
                if len(confidence.shape) > 1:
                    confidence = np.max(confidence, axis=1)
                logger.info("Confidence scores calculated from decision function")
                return confidence

            else:
                logger.warning("Model does not support confidence calculation")
                return None

        except Exception as e:
            logger.error(f"Failed to calculate confidence scores: {str(e)}")
            return None

    def _get_explanations(self, X: np.ndarray) -> Optional[Any]:
        """Generate model explanations (requires additional libraries)"""

        try:
            # Try to use SHAP for explanations
            try:
                import shap

                # Create explainer based on model type
                if hasattr(self.model, 'predict_proba'):
                    explainer = shap.Explainer(self.model.predict_proba, X[:100])  # Sample for background
                else:
                    explainer = shap.Explainer(self.model.predict, X[:100])

                # Calculate SHAP values for first few samples (expensive operation)
                sample_size = min(10, X.shape[0])
                shap_values = explainer(X[:sample_size])

                logger.info(f"Generated SHAP explanations for {sample_size} samples")
                return shap_values

            except ImportError:
                logger.warning("SHAP not available for explanations")

            # Try to use feature importance if available
            if hasattr(self.model, 'feature_importances_'):
                logger.info("Using feature importance as explanation")
                return {
                    'feature_importances': self.model.feature_importances_,
                    'explanation_type': 'feature_importance'
                }

            logger.warning("No explanation method available")
            return None

        except Exception as e:
            logger.error(f"Failed to generate explanations: {str(e)}")
            return None

    def get_confidence_scores(self) -> Optional[np.ndarray]:
        """Get the confidence scores from last prediction"""
        return self.confidence_scores

    def get_explanations(self) -> Optional[Any]:
        """Get the explanations from last prediction"""
        return self.explanations

    def predict_single(self, sample: np.ndarray) -> Union[float, int, str]:
        """Make prediction on a single sample"""

        if len(sample.shape) == 1:
            sample = sample.reshape(1, -1)

        prediction = self.predict(sample)[0]
        return prediction

    def get_model_info(self) -> dict:
        """Get information about the loaded model"""

        if self.model is None:
            return {"error": "No model loaded"}

        info = {
            "model_type": type(self.model).__name__,
            "model_path": str(self.model_path),
            "supports_probability": hasattr(self.model, 'predict_proba'),
            "supports_decision_function": hasattr(self.model, 'decision_function'),
            "supports_feature_importance": hasattr(self.model, 'feature_importances_')
        }

        # Add model-specific parameters if available
        if hasattr(self.model, 'get_params'):
            info["parameters"] = self.model.get_params()

        return info

