#!/usr/bin/env python3
"""
Model training module for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

import joblib
import numpy as np
from pathlib import Path
from typing import Any, Dict, Optional, Tuple
from sklearn.ensemble import RandomForestClassifier, RandomForestRegressor
from sklearn.metrics import accuracy_score, precision_score, recall_score, f1_score
from sklearn.metrics import mean_squared_error, mean_absolute_error, r2_score
import logging

logger = logging.getLogger(__name__)

class Trainer:
    """Model training and evaluation class"""

    def __init__(self, config=None):
        """Initialize trainer with configuration"""
        self.config = config
        self.model = None
        self.training_history = []

    def _get_model(self):
        """Get model based on configuration"""
        if not self.config:
            # Default model
            return RandomForestClassifier(random_state=42)

        algorithm = self.config.algorithm
        model_type = self.config.model_type
        params = self.config.model_params

        if algorithm == "random_forest":
            if model_type == "classification":
                return RandomForestClassifier(**params)
            else:
                return RandomForestRegressor(**params)

        # Add more algorithms here
        elif algorithm == "xgboost":
            try:
                import xgboost as xgb
                if model_type == "classification":
                    return xgb.XGBClassifier(**params)
                else:
                    return xgb.XGBRegressor(**params)
            except ImportError:
                logger.warning("XGBoost not installed, falling back to RandomForest")
                return self._get_model()

        else:
            logger.warning(f"Unknown algorithm: {algorithm}, using RandomForest")
            if model_type == "classification":
                return RandomForestClassifier(**params)
            else:
                return RandomForestRegressor(**params)

    def train(self, X_train: np.ndarray, y_train: np.ndarray, 
              X_val: np.ndarray = None, y_val: np.ndarray = None,
              epochs: int = None, batch_size: int = None,
              save_best: bool = False, early_stopping: bool = False,
              verbose: bool = True, metrics_tracker=None) -> Any:
        """Train the model"""

        logger.info("Starting model training...")

        # Get model
        self.model = self._get_model()

        # Train model
        logger.info(f"Training {type(self.model).__name__}...")
        self.model.fit(X_train, y_train)

        # Evaluate on training data
        train_score = self.model.score(X_train, y_train)
        logger.info(f"Training score: {train_score:.4f}")

        # Evaluate on validation data if provided
        if X_val is not None and y_val is not None:
            val_score = self.model.score(X_val, y_val)
            logger.info(f"Validation score: {val_score:.4f}")

            # Store training history
            self.training_history.append({
                'epoch': 1,  # Simplified for non-neural models
                'train_score': train_score,
                'val_score': val_score
            })

        logger.info("âœ… Training completed!")
        return self.model

    def evaluate(self, model: Any, X_test: np.ndarray, y_test: np.ndarray, 
                metrics_tracker=None) -> Dict[str, float]:
        """Evaluate the model"""

        logger.info("Evaluating model...")

        # Make predictions
        y_pred = model.predict(X_test)

        metrics = {}

        if self.config and self.config.model_type == "classification":
            # Classification metrics
            metrics['accuracy'] = accuracy_score(y_test, y_pred)
            metrics['precision'] = precision_score(y_test, y_pred, average='weighted')
            metrics['recall'] = recall_score(y_test, y_pred, average='weighted')
            metrics['f1_score'] = f1_score(y_test, y_pred, average='weighted')

            logger.info(f"Accuracy: {metrics['accuracy']:.4f}")
            logger.info(f"Precision: {metrics['precision']:.4f}")
            logger.info(f"Recall: {metrics['recall']:.4f}")
            logger.info(f"F1 Score: {metrics['f1_score']:.4f}")

        else:
            # Regression metrics
            metrics['mse'] = mean_squared_error(y_test, y_pred)
            metrics['rmse'] = np.sqrt(metrics['mse'])
            metrics['mae'] = mean_absolute_error(y_test, y_pred)
            metrics['r2_score'] = r2_score(y_test, y_pred)

            logger.info(f"MSE: {metrics['mse']:.4f}")
            logger.info(f"RMSE: {metrics['rmse']:.4f}")
            logger.info(f"MAE: {metrics['mae']:.4f}")
            logger.info(f"RÂ² Score: {metrics['r2_score']:.4f}")

        # Update metrics tracker if provided
        if metrics_tracker:
            metrics_tracker.update(metrics)

        return metrics

    def save_model(self, model: Any, save_path: str) -> str:
        """Save the trained model"""

        save_path = Path(save_path)
        save_path.parent.mkdir(parents=True, exist_ok=True)

        # Add .joblib extension if not present
        if save_path.suffix != '.joblib':
            if save_path.is_dir():
                save_path = save_path / "model.joblib"
            else:
                save_path = save_path.with_suffix('.joblib')

        logger.info(f"Saving model to: {save_path}")
        joblib.dump(model, save_path)

        return str(save_path)

    def load_model(self, model_path: str) -> Any:
        """Load a saved model"""

        model_path = Path(model_path)
        if not model_path.exists():
            raise FileNotFoundError(f"Model file not found: {model_path}")

        logger.info(f"Loading model from: {model_path}")
        model = joblib.load(model_path)

        return model

    def get_feature_importance(self, model: Any = None) -> Optional[np.ndarray]:
        """Get feature importance if supported by the model"""

        if model is None:
            model = self.model

        if hasattr(model, 'feature_importances_'):
            return model.feature_importances_
        else:
            logger.warning("Model does not support feature importance")
            return None

    def cross_validate(self, X: np.ndarray, y: np.ndarray, cv_folds: int = 5) -> Dict[str, float]:
        """Perform cross-validation"""

        from sklearn.model_selection import cross_val_score

        logger.info(f"Performing {cv_folds}-fold cross-validation...")

        model = self._get_model()
        scores = cross_val_score(model, X, y, cv=cv_folds)

        cv_results = {
            'mean_score': scores.mean(),
            'std_score': scores.std(),
            'scores': scores.tolist()
        }

        logger.info(f"CV Score: {cv_results['mean_score']:.4f} (+/- {cv_results['std_score'] * 2:.4f})")

        return cv_results

