"""
Metrics tracking utilities for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

import json
from pathlib import Path
from typing import Dict, Any, List
from datetime import datetime
import logging

logger = logging.getLogger(__name__)

class MetricsTracker:
    """Track and manage ML metrics"""

    def __init__(self):
        """Initialize metrics tracker"""
        self.metrics = {}
        self.history = []
        self.start_time = datetime.now()

    def update(self, metrics: Dict[str, float]):
        """Update metrics dictionary"""
        self.metrics.update(metrics)

        # Add timestamp
        timestamped_metrics = {
            "timestamp": datetime.now().isoformat(),
            **metrics
        }
        self.history.append(timestamped_metrics)

        logger.info(f"Metrics updated: {metrics}")

    def get_metric(self, name: str) -> Any:
        """Get specific metric value"""
        return self.metrics.get(name)

    def get_all_metrics(self) -> Dict[str, Any]:
        """Get all current metrics"""
        return self.metrics.copy()

    def get_summary(self) -> Dict[str, Any]:
        """Get metrics summary"""
        summary = {
            "current_metrics": self.metrics,
            "total_updates": len(self.history),
            "tracking_duration": str(datetime.now() - self.start_time)
        }

        if self.history:
            summary["first_update"] = self.history[0]["timestamp"]
            summary["last_update"] = self.history[-1]["timestamp"]

        return summary

    def save_history(self, file_path: str):
        """Save metrics history to file"""
        file_path = Path(file_path)
        file_path.parent.mkdir(parents=True, exist_ok=True)

        history_data = {
            "metrics_history": self.history,
            "summary": self.get_summary(),
            "exported_at": datetime.now().isoformat()
        }

        with open(file_path, 'w') as f:
            json.dump(history_data, f, indent=2)

        logger.info(f"Metrics history saved to: {file_path}")

    def load_history(self, file_path: str):
        """Load metrics history from file"""
        file_path = Path(file_path)

        if not file_path.exists():
            logger.warning(f"Metrics file not found: {file_path}")
            return

        with open(file_path, 'r') as f:
            data = json.load(f)

        self.history = data.get("metrics_history", [])
        if self.history:
            # Restore latest metrics
            latest = self.history[-1]
            self.metrics = {k: v for k, v in latest.items() if k != "timestamp"}

        logger.info(f"Metrics history loaded from: {file_path}")

    def compare_metrics(self, other_metrics: Dict[str, float]) -> Dict[str, float]:
        """Compare current metrics with other metrics"""
        comparison = {}

        for metric_name in self.metrics:
            if metric_name in other_metrics:
                current_value = self.metrics[metric_name]
                other_value = other_metrics[metric_name]
                difference = current_value - other_value
                comparison[f"{metric_name}_diff"] = difference
                comparison[f"{metric_name}_improvement"] = difference > 0

        return comparison

    def get_best_metrics(self) -> Dict[str, Any]:
        """Get best values for each metric from history"""
        if not self.history:
            return {}

        best_metrics = {}

        # Collect all metric names
        all_metrics = set()
        for entry in self.history:
            all_metrics.update(entry.keys())
        all_metrics.discard("timestamp")

        # Find best value for each metric
        for metric_name in all_metrics:
            values = []
            for entry in self.history:
                if metric_name in entry:
                    try:
                        values.append(float(entry[metric_name]))
                    except (ValueError, TypeError):
                        continue

            if values:
                # Assume higher is better for most metrics (customize as needed)
                if metric_name.lower() in ["mse", "rmse", "mae", "loss", "error"]:
                    best_metrics[f"best_{metric_name}"] = min(values)
                else:
                    best_metrics[f"best_{metric_name}"] = max(values)

        return best_metrics

    def reset(self):
        """Reset all metrics and history"""
        self.metrics.clear()
        self.history.clear()
        self.start_time = datetime.now()
        logger.info("Metrics tracker reset")

