#!/usr/bin/env python3
"""
MongoDB connection management for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

import logging
from typing import Optional
from mongoengine import connect, disconnect, connection
from pymongo.errors import ServerSelectionTimeoutError, ConnectionFailure
from pymongo import MongoClient

logger = logging.getLogger(__name__)

class MongoManager:
    """
    MongoDB connection and database management

    Handles MongoDB connections, database operations, and connection testing.
    """

    def __init__(self, config):
        """Initialize MongoDB manager with configuration"""
        self.config = config
        self.client = None
        self.database = None
        self._connected = False

    def connect(self):
        """Connect to MongoDB using MongoEngine"""
        try:
            # Disconnect any existing connections
            self.disconnect()

            # Connection parameters
            connection_params = {
                'host': self.config.get_connection_string(),
                'connect': True,
                'serverSelectionTimeoutMS': self.config.server_selection_timeout,
                'socketTimeoutMS': self.config.socket_timeout,
                'maxPoolSize': self.config.max_pool_size,
                'minPoolSize': self.config.min_pool_size,
            }

            # Add authentication if provided
            if self.config.username and self.config.password:
                connection_params.update({
                    'username': self.config.username,
                    'password': self.config.password,
                    'authentication_source': self.config.auth_source,
                })

            # Add SSL configuration if enabled
            if self.config.use_ssl:
                connection_params.update({
                    'ssl': True,
                    'ssl_cert_reqs': self.config.ssl_cert_reqs,
                })

                if self.config.ssl_ca_certs:
                    connection_params['ssl_ca_certs'] = self.config.ssl_ca_certs

                if self.config.ssl_certfile:
                    connection_params['ssl_certfile'] = self.config.ssl_certfile

                if self.config.ssl_keyfile:
                    connection_params['ssl_keyfile'] = self.config.ssl_keyfile

            # Connect to MongoDB
            self.client = connect(
                db=self.config.database_name,
                alias='default',
                **connection_params
            )

            self.database = self.client[self.config.database_name]
            self._connected = True

            logger.info(f"Connected to MongoDB: {self.config.database_name}")

        except Exception as e:
            logger.error(f"Failed to connect to MongoDB: {str(e)}")
            raise

    def disconnect(self):
        """Disconnect from MongoDB"""
        try:
            disconnect()
            self._connected = False
            logger.info("Disconnected from MongoDB")
        except Exception as e:
            logger.warning(f"Error during MongoDB disconnect: {str(e)}")

    def test_connection(self) -> bool:
        """Test MongoDB connection"""
        try:
            if not self._connected:
                return False

            # Get MongoDB client and test connection
            client = connection.get_connection()

            # Simple ping to test connection
            client.admin.command('ping')

            logger.info("MongoDB connection test successful")
            return True

        except (ServerSelectionTimeoutError, ConnectionFailure) as e:
            logger.error(f"MongoDB connection test failed: {str(e)}")
            return False
        except Exception as e:
            logger.error(f"Unexpected error during connection test: {str(e)}")
            return False

    def get_database_info(self) -> dict:
        """Get information about the database"""
        try:
            if not self._connected:
                raise ConnectionError("Not connected to MongoDB")

            client = connection.get_connection()
            db = client[self.config.database_name]

            # Get database stats
            stats = db.command("dbStats")

            # Get collection names
            collections = db.list_collection_names()

            # Get indexes for each collection
            collection_info = {}
            for collection_name in collections:
                collection = db[collection_name]
                indexes = list(collection.list_indexes())
                collection_info[collection_name] = {
                    'document_count': collection.count_documents({}),
                    'indexes': [index['name'] for index in indexes]
                }

            return {
                'database_name': self.config.database_name,
                'collections': collections,
                'collection_info': collection_info,
                'database_stats': {
                    'size_bytes': stats.get('dataSize', 0),
                    'index_size_bytes': stats.get('indexSize', 0),
                    'storage_size_bytes': stats.get('storageSize', 0),
                    'object_count': stats.get('objects', 0),
                    'collection_count': stats.get('collections', 0),
                    'index_count': stats.get('indexes', 0),
                }
            }

        except Exception as e:
            logger.error(f"Error getting database info: {str(e)}")
            return {}

    def create_indexes(self, model_classes: list):
        """Create indexes for model classes"""
        try:
            for model_class in model_classes:
                if hasattr(model_class, '_get_collection'):
                    collection = model_class._get_collection()

                    # Get indexes defined in the model
                    if hasattr(model_class, '_meta') and hasattr(model_class._meta, 'indexes'):
                        for index in model_class._meta.indexes:
                            try:
                                collection.create_index(index)
                                logger.info(f"Created index for {model_class.__name__}: {index}")
                            except Exception as e:
                                logger.warning(f"Failed to create index for {model_class.__name__}: {str(e)}")

            logger.info("Index creation completed")

        except Exception as e:
            logger.error(f"Error creating indexes: {str(e)}")
            raise

    def drop_database(self):
        """Drop the entire database"""
        try:
            if not self._connected:
                raise ConnectionError("Not connected to MongoDB")

            client = connection.get_connection()
            client.drop_database(self.config.database_name)

            logger.info(f"Database '{self.config.database_name}' dropped successfully")

        except Exception as e:
            logger.error(f"Error dropping database: {str(e)}")
            raise

    def backup_database(self, backup_path: str):
        """Create database backup using mongodump"""
        import subprocess

        try:
            cmd = [
                'mongodump',
                '--host', f"{self.config.host}:{self.config.port}",
                '--db', self.config.database_name,
                '--out', backup_path
            ]

            if self.config.username:
                cmd.extend(['--username', self.config.username])

            if self.config.password:
                cmd.extend(['--password', self.config.password])

            if self.config.auth_source:
                cmd.extend(['--authenticationDatabase', self.config.auth_source])

            result = subprocess.run(cmd, capture_output=True, text=True)

            if result.returncode == 0:
                logger.info(f"Database backup created successfully: {backup_path}")
            else:
                logger.error(f"Database backup failed: {result.stderr}")
                raise Exception(f"Backup failed: {result.stderr}")

        except FileNotFoundError:
            logger.error("mongodump command not found. Please install MongoDB tools.")
            raise
        except Exception as e:
            logger.error(f"Error creating database backup: {str(e)}")
            raise

    def restore_database(self, backup_path: str):
        """Restore database from backup using mongorestore"""
        import subprocess

        try:
            cmd = [
                'mongorestore',
                '--host', f"{self.config.host}:{self.config.port}",
                '--db', self.config.database_name,
                '--drop',  # Drop existing collections before restore
                backup_path
            ]

            if self.config.username:
                cmd.extend(['--username', self.config.username])

            if self.config.password:
                cmd.extend(['--password', self.config.password])

            if self.config.auth_source:
                cmd.extend(['--authenticationDatabase', self.config.auth_source])

            result = subprocess.run(cmd, capture_output=True, text=True)

            if result.returncode == 0:
                logger.info(f"Database restored successfully from: {backup_path}")
            else:
                logger.error(f"Database restore failed: {result.stderr}")
                raise Exception(f"Restore failed: {result.stderr}")

        except FileNotFoundError:
            logger.error("mongorestore command not found. Please install MongoDB tools.")
            raise
        except Exception as e:
            logger.error(f"Error restoring database: {str(e)}")
            raise

    def get_client(self):
        """Get PyMongo client"""
        if self._connected:
            return connection.get_connection()
        return None

    def get_database(self):
        """Get database instance"""
        if self._connected:
            client = connection.get_connection()
            return client[self.config.database_name]
        return None

    def is_connected(self) -> bool:
        """Check if connected to MongoDB"""
        return self._connected

