#!/usr/bin/env python3
"""
Database configuration for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

import os
from typing import Optional
from dotenv import load_dotenv

# Load environment variables
load_dotenv()

class DatabaseConfig:
    """Database configuration management"""

    def __init__(self):
        """Initialize database configuration"""
        self.database_type = os.getenv('DATABASE_TYPE', 'sqlite')
        self.database_host = os.getenv('DATABASE_HOST', 'localhost')
        self.database_port = os.getenv('DATABASE_PORT', self._get_default_port())
        self.database_name = os.getenv('DATABASE_NAME', '{{ project_name }}_db')
        self.database_user = os.getenv('DATABASE_USER', 'user')
        self.database_password = os.getenv('DATABASE_PASSWORD', 'password')

        # SQLAlchemy specific settings
        self.echo_sql = os.getenv('DATABASE_ECHO', 'False').lower() == 'true'
        self.development_mode = os.getenv('ENVIRONMENT', 'development') == 'development'

        # Connection settings
        self.pool_size = int(os.getenv('DATABASE_POOL_SIZE', '5'))
        self.max_overflow = int(os.getenv('DATABASE_MAX_OVERFLOW', '10'))
        self.pool_timeout = int(os.getenv('DATABASE_POOL_TIMEOUT', '30'))

    def _get_default_port(self) -> str:
        """Get default port based on database type"""
        ports = {
            'postgresql': '5432',
            'mysql': '3306',
            'sqlite': '',
            'oracle': '1521',
            'mssql': '1433'
        }
        return ports.get(self.database_type, '5432')

    def get_database_url(self) -> str:
        """
        Generate database URL based on configuration

        Returns:
            Complete database URL for SQLAlchemy
        """
        if self.database_type == 'sqlite':
            # SQLite database file path
            db_path = os.getenv('DATABASE_PATH', 'data/{{ project_name }}.db')
            return f"sqlite:///{db_path}"

        elif self.database_type == 'postgresql':
            return (
                f"postgresql://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
            )

        elif self.database_type == 'mysql':
            return (
                f"mysql+pymysql://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
            )

        elif self.database_type == 'oracle':
            return (
                f"oracle+cx_oracle://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
            )

        elif self.database_type == 'mssql':
            return (
                f"mssql+pyodbc://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
                f"?driver=ODBC+Driver+17+for+SQL+Server"
            )

        else:
            raise ValueError(f"Unsupported database type: {self.database_type}")

    def get_async_database_url(self) -> str:
        """
        Generate async database URL for async operations

        Returns:
            Async database URL for SQLAlchemy
        """
        if self.database_type == 'postgresql':
            return (
                f"postgresql+asyncpg://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
            )

        elif self.database_type == 'mysql':
            return (
                f"mysql+aiomysql://{self.database_user}:{self.database_password}"
                f"@{self.database_host}:{self.database_port}/{self.database_name}"
            )

        elif self.database_type == 'sqlite':
            db_path = os.getenv('DATABASE_PATH', 'data/{{ project_name }}.db')
            return f"sqlite+aiosqlite:///{db_path}"

        else:
            raise ValueError(f"Async not supported for database type: {self.database_type}")

    def get_alembic_config(self) -> dict:
        """Get configuration for Alembic migrations"""
        return {
            'sqlalchemy.url': self.get_database_url(),
            'script_location': 'src/migrations',
            'file_template': '%%(year)d_%%(month).2d_%%(day).2d_%%(hour).2d%%(minute).2d_%%(rev)s_%%(slug)s',
            'timezone': 'UTC',
        }

    def validate_config(self) -> bool:
        """Validate database configuration"""
        if not self.database_type:
            raise ValueError("DATABASE_TYPE must be specified")

        if self.database_type != 'sqlite':
            if not self.database_host:
                raise ValueError("DATABASE_HOST must be specified for non-SQLite databases")

            if not self.database_name:
                raise ValueError("DATABASE_NAME must be specified")

            if not self.database_user:
                raise ValueError("DATABASE_USER must be specified")

            if not self.database_password:
                raise ValueError("DATABASE_PASSWORD must be specified")

        return True

    def __repr__(self):
        """String representation of configuration"""
        return f"DatabaseConfig(type={self.database_type}, host={self.database_host})"

