#!/usr/bin/env python3
"""
Base model and database setup for {{ project_name }}
Created by {{ generator }} at {{ timestamp }}
"""

from sqlalchemy import Column, Integer, DateTime, String
from sqlalchemy.ext.declarative import declarative_base
from sqlalchemy.sql import func
from datetime import datetime
from typing import Dict, Any

# Create base class for all models
Base = declarative_base()

class BaseModel(Base):
    """
    Base model class with common fields and methods

    All models should inherit from this class to get:
    - id field (primary key)
    - created_at timestamp
    - updated_at timestamp
    - Common utility methods
    """

    __abstract__ = True

    # Common fields
    id = Column(Integer, primary_key=True, index=True)
    created_at = Column(DateTime(timezone=True), server_default=func.now(), nullable=False)
    updated_at = Column(DateTime(timezone=True), server_default=func.now(), onupdate=func.now(), nullable=False)

    def to_dict(self, exclude_fields: list = None) -> Dict[str, Any]:
        """
        Convert model instance to dictionary

        Args:
            exclude_fields: List of field names to exclude

        Returns:
            Dictionary representation of the model
        """
        exclude_fields = exclude_fields or []

        result = {}
        for column in self.__table__.columns:
            if column.name not in exclude_fields:
                value = getattr(self, column.name)

                # Handle datetime serialization
                if isinstance(value, datetime):
                    result[column.name] = value.isoformat()
                else:
                    result[column.name] = value

        return result

    def update_from_dict(self, data: Dict[str, Any], exclude_fields: list = None):
        """
        Update model instance from dictionary

        Args:
            data: Dictionary with field values
            exclude_fields: List of field names to exclude from update
        """
        exclude_fields = exclude_fields or ['id', 'created_at']

        for key, value in data.items():
            if key not in exclude_fields and hasattr(self, key):
                setattr(self, key, value)

    def __repr__(self):
        """String representation of the model"""
        return f"<{self.__class__.__name__}(id={self.id})>"

    def save(self, session):
        """Save model instance to database"""
        session.add(self)
        session.commit()
        session.refresh(self)
        return self

    def delete(self, session):
        """Delete model instance from database"""
        session.delete(self)
        session.commit()

    @classmethod
    def get_by_id(cls, session, model_id: int):
        """Get model instance by ID"""
        return session.query(cls).filter(cls.id == model_id).first()

    @classmethod
    def get_all(cls, session, limit: int = None, offset: int = None):
        """Get all model instances"""
        query = session.query(cls)

        if offset:
            query = query.offset(offset)

        if limit:
            query = query.limit(limit)

        return query.all()

    @classmethod
    def count(cls, session) -> int:
        """Count total number of instances"""
        return session.query(cls).count()

    @classmethod
    def create(cls, session, **kwargs):
        """Create new model instance"""
        instance = cls(**kwargs)
        return instance.save(session)

class TimestampMixin:
    """Mixin for models that need timestamp fields"""

    created_at = Column(DateTime(timezone=True), server_default=func.now(), nullable=False)
    updated_at = Column(DateTime(timezone=True), server_default=func.now(), onupdate=func.now(), nullable=False)

class SoftDeleteMixin:
    """Mixin for models that need soft delete functionality"""

    deleted_at = Column(DateTime(timezone=True), nullable=True)
    is_deleted = Column(String(1), default='N', nullable=False)

    def soft_delete(self, session):
        """Soft delete the model instance"""
        self.deleted_at = func.now()
        self.is_deleted = 'Y'
        session.commit()

    def restore(self, session):
        """Restore soft deleted model instance"""
        self.deleted_at = None
        self.is_deleted = 'N'
        session.commit()

    @classmethod
    def get_active(cls, session):
        """Get all active (non-deleted) instances"""
        return session.query(cls).filter(cls.is_deleted == 'N').all()

    @classmethod
    def get_deleted(cls, session):
        """Get all soft deleted instances"""
        return session.query(cls).filter(cls.is_deleted == 'Y').all()
